import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Box,
  Button,
  Paper,
  Alert,
  CircularProgress
} from '@mui/material';
import { useAuth } from '../contexts/AuthContext';
import BasicInfoStep from '../components/submission/BasicInfoStep';
import IngredientsStep from '../components/submission/IngredientsStep';
import HealthClaimsStep from '../components/submission/HealthClaimsStep';
import DocumentsStep from '../components/submission/DocumentsStep';
import ReviewStep from '../components/submission/ReviewStep';

const steps = [
  'Basic Information',
  'Ingredients & Suppliers',
  'Health Claims & Evidence',
  'Supporting Documents',
  'Review & Submit'
];

const SupplementForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isDraft, setIsDraft] = useState(true);

  // Form data state following OSI schema structure
  const [formData, setFormData] = useState({
    // Basic product information
    artgEntry: {
      artgNumber: '',
      productName: '',
      alternateNames: [],
      sponsor: '',
      postalAddress: '',
      productCategory: 'Medicine',
      status: 'Draft'
    },
    products: [{
      productName: '',
      alternateNames: [],
      productType: 'Single Medicine Product',
      effectiveDate: new Date().toISOString().split('T')[0]
    }],
    permittedIndications: [],
    indicationRequirements: [],
    warnings: [],
    dosageInformation: {
      adults: '',
      children: '',
      generalNotes: ''
    },
    allergenInformation: {
      containsAllergens: [],
      freeOfClaims: [],
      allergenStatement: '',
      crossContaminationRisk: null
    },
    additionalProductInformation: {
      packSizeInformation: {
        packSize: '',
        poisonSchedule: ''
      }
    },
    components: [{
      formulation: 'Primary',
      dosageForm: '',
      routeOfAdministration: '',
      visualIdentification: '',
      activeIngredients: [],
      excipients: []
    }],
    clinicalTrials: [],
    evidenceRegulatorySummary: {
      overallEvidenceStatement: '',
      keyRegulatoryPoints: [],
      evidenceGradingSystemUsed: '',
      references: []
    },
    interactions: [],
    contraindicationsAdverseEffects: {
      contraindications: [],
      adverseEffects: []
    },
    intendedPopulation: {
      primaryTargetGroups: [],
      ageRange: {
        minAge: null,
        maxAge: null,
        unit: 'years'
      },
      sex: 'Any',
      specificConsiderations: ''
    },
    storageShelfLife: {
      storageConditions: '',
      shelfLifeMonths: null,
      useByInstructions: '',
      batchNumberFormat: '',
      expiryDateFormat: ''
    },
    productIdentifiers: [],
    documentInformation: {
      dataEntrySource: 'Manufacturer Submission',
      dataEntryDate: new Date().toISOString().split('T')[0],
      version: '0.2.0',
      notes: ''
    },
    // Additional fields for submission tracking
    submissionMetadata: {
      submittedBy: user?.id || '',
      organizationId: user?.organizationId || '',
      status: 'draft',
      lastSaved: null,
      submittedAt: null
    }
  });

  // Load existing supplement data if editing
  useEffect(() => {
    if (id && id !== 'new') {
      loadSupplementData(id);
    }
  }, [id]);

  const loadSupplementData = async (supplementId) => {
    try {
      setLoading(true);
      const response = await api.get(`/supplements/${supplementId}`);
      const data = response.data.supplement;
      setFormData(data.osi_data || data);
      setIsDraft(data.status === 'draft');
    } catch (err) {
      console.error('Error loading supplement data:', err);
      setError(err.response?.data?.error || 'Error loading supplement data');
    } finally {
      setLoading(false);
    }
  };

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepClick = (step) => {
    setActiveStep(step);
  };

  const updateFormData = (section, data) => {
    setFormData(prev => ({
      ...prev,
      [section]: data
    }));
  };

  const saveDraft = async () => {
    try {
      setLoading(true);
      
      // Prepare data for draft submission - make it more lenient
      const draftData = {
        ...formData,
        // Ensure required fields have default values for draft
        artgEntry: {
          ...formData.artgEntry,
          artgNumber: formData.artgEntry.artgNumber || 'DRAFT-' + Date.now(),
          status: 'Draft'
        },
        // Convert warnings objects to strings for schema compliance
        warnings: formData.warnings.map(w => typeof w === 'object' ? w.text : w),
        // Ensure components have proper structure
        components: formData.components.map(comp => ({
          ...comp,
          activeIngredients: comp.activeIngredients.map(ing => ({
            ...ing,
            // Convert quantity object to string for schema compliance
            quantity: typeof ing.quantity === 'object' 
              ? `${ing.quantity.value} ${ing.quantity.unit}`
              : ing.quantity,
            // Remove equivalentTo if it's null or empty
            ...(ing.equivalentTo && ing.equivalentTo.substance ? {
              equivalentTo: `${ing.equivalentTo.value} ${ing.equivalentTo.unit} ${ing.equivalentTo.substance}`
            } : {})
          }))
        })),
        submissionMetadata: {
          ...formData.submissionMetadata,
          lastSaved: new Date().toISOString(),
          status: 'draft'
        }
      };

      const url = id && id !== 'new' ? `/api/supplements/${id}` : '/api/supplements';
      const method = id && id !== 'new' ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(draftData)
      });

      if (response.ok) {
        const result = await response.json();
        setSuccess('Draft saved successfully');
        if (!id || id === 'new') {
          navigate(`/supplement/${result.id}/edit`);
        }
      } else {
        const errorData = await response.json();
        console.error('Save draft error:', errorData);
        setError(errorData.message || 'Failed to save draft');
      }
    } catch (err) {
      console.error('Save draft error:', err);
      setError('Error saving draft');
    } finally {
      setLoading(false);
    }
  };

  const submitForReview = async () => {
    try {
      setLoading(true);
      const payload = {
        ...formData,
        submissionMetadata: {
          ...formData.submissionMetadata,
          submittedAt: new Date().toISOString(),
          status: 'submitted'
        }
      };

      const url = id && id !== 'new' ? `/api/supplements/${id}/submit` : '/api/supplements/submit';
      const method = 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(payload)
      });

      if (response.ok) {
        setSuccess('Supplement submitted for review successfully!');
        setTimeout(() => {
          navigate('/dashboard');
        }, 2000);
      } else {
        const errorData = await response.json();
        setError(errorData.message || 'Failed to submit supplement');
      }
    } catch (err) {
      setError('Error submitting supplement');
    } finally {
      setLoading(false);
    }
  };

  const getStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <BasicInfoStep
            data={formData}
            updateData={updateFormData}
            onNext={handleNext}
          />
        );
      case 1:
        return (
          <IngredientsStep
            data={formData}
            updateData={updateFormData}
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 2:
        return (
          <HealthClaimsStep
            data={formData}
            updateData={updateFormData}
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 3:
        return (
          <DocumentsStep
            data={formData}
            updateData={updateFormData}
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 4:
        return (
          <ReviewStep
            data={formData}
            onSubmit={submitForReview}
            onBack={handleBack}
            onSaveDraft={saveDraft}
            loading={loading}
          />
        );
      default:
        return 'Unknown step';
    }
  };

  if (loading && !formData.artgEntry.productName) {
    return (
      <Container>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          {id && id !== 'new' ? 'Edit Product Submission' : 'New Product Submission'}
        </Typography>
        <Typography variant="body1" color="text.secondary" gutterBottom>
          Complete all steps to submit your product for OSI certification
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 3 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <Paper sx={{ p: 3 }}>
        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label, index) => (
            <Step key={label} completed={index < activeStep}>
              <StepLabel 
                onClick={() => handleStepClick(index)}
                sx={{ cursor: 'pointer' }}
              >
                {label}
              </StepLabel>
            </Step>
          ))}
        </Stepper>

        <Box sx={{ minHeight: '400px' }}>
          {getStepContent(activeStep)}
        </Box>

        {/* Save Draft Button - Available on all steps except final review */}
        {activeStep < steps.length - 1 && (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
            <Button
              variant="outlined"
              onClick={saveDraft}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : null}
            >
              Save Draft
            </Button>
          </Box>
        )}
      </Paper>
    </Container>
  );
};

export default SupplementForm;
